Layer.InitCommand:
%function(self)
			if not self:GetTexture() then
				self:SetWidth( PREFSMAN:GetPreference('DisplayWidth') );
				self:SetHeight( PREFSMAN:GetPreference('DisplayHeight') );
				self:EnableDepthBuffer( false );
				self:EnableAlphaBuffer( true );
				self:EnableFloat( false );
				self:EnablePreserveTexture( true );

				self:hidden(1);

				self:Create();
			end
		end

Layer.OverlayReadyMessageCommand:
%function(self)
			self:SetTextureFiltering(true)
			self:basezoomx(SCREEN_WIDTH/DISPLAY:GetDisplayWidth());
			self:basezoomy(-SCREEN_HEIGHT/DISPLAY:GetDisplayHeight());
			self:xy(SCREEN_CENTER_X,SCREEN_CENTER_Y);
			self:sleep(0.01);
			self:hidden(1)
			self:queuecommand('Set');
			self:GetShader():uniform1f( 'howGray', 1.0 )

		end
